/**
 * Date in ISO 8601 format (e.g., "2024-12-20T10:30:00.000Z")
 */
type ISO8601String = string;
/**
 * UUID as string
 */
type UUIDString = string;

type DayOfWeek = "MONDAY" | "TUESDAY" | "WEDNESDAY" | "THURSDAY" | "FRIDAY" | "SATURDAY" | "SUNDAY";

type Callback = () => void;

type Pagination = {
  page: number;
  pageSize: number;
};

interface Window {
  jQuery: any;
  angular: {
    element(element: Element): {
      scope(): any;
    };
  };
  [key: string]: any;
}
